property.code:
require(['jquery'], function($) {
  dlURL = $('#downloadURL').val() + "?download=1"
  sendRepURL = $('#downloadURL').val() + "?confirm=1&xpage=plain"

  $("#send-report").on('click', function(event, data) {
    event.preventDefault();
    sendReport();
  });

  $("#download-report").on('click', function(event, data) {
    event.preventDefault();
    window.location=dlURL;
  });

  function sendReport(){
   if(!confirm("Your are about to send a configuration report to the XWiki Support Team.")) {
      return false;
    }else {
      new XWiki.widgets.Notification('The reports have started sending', 'info');
      $.ajax({url: sendRepURL, success: function(result){
        $("#st-content").html(result);
        $("#download-sent-report").on('click', function(event, data) {
          event.preventDefault();
          var dl = $('#dlSentURL').val() + $(this).attr('href');
          window.location=dl;
        });
      }});
    }
  }
});

