 def st = xwiki.parseGroovyFromPage("SupportToolsCode.Groovy")
 st.setXWiki(xwiki, xcontext, services)
 def jobId = request.id;
 if (jobId && jobId!="") {
  println(services.localization.render("supporttools.downloadreport.downloading"));
  st.downloadReport(jobId)
 } else {
  println(services.localization.render("supporttools.downloadreport.nojob"));
  println st.listReports();
 }
